const radios=document.querySelectorAll('input[name="sexo"]'),icone=document.getElementById("icone-sexo");radios.forEach(e=>{e.addEventListener("change",()=>{"masculino"===e.value?icone.style.backgroundImage="url('img/masculino.webp')":"feminino"===e.value&&(icone.style.backgroundImage="url('img/feminino.webp')")})});